import { Component, OnInit } from '@angular/core';

import { Sale } from '../../model/sale';
import { SaleService } from '../../services/sale.service';

@Component({
  selector: 'app-sales',
  templateUrl: './sales.component.html',
  styleUrls: ['./sales.component.css']
})
export class SalesComponent implements OnInit {
  sales: Sale[] = [];

  constructor(private saleService: SaleService) { }

  ngOnInit(): void {
    this.getSales();
  }

  getSales(): void {
    this.saleService.getSales()
    .subscribe(sales => this.sales = sales);
  }

  add(dateSale: string, qquantity: string, idC: string, idM: string): void {
    dateSale = dateSale.trim();
    let date_sale = new Date(dateSale);
    qquantity = qquantity.trim();
    idC = idC.trim();
    idM = idM.trim();
    let quantity=Number(qquantity);
    let id_client=Number(idC);
    let id_medicament=Number(idM);

    if (!date_sale || !quantity || !id_client || !id_medicament) { return; }
    this.saleService.addSale({ date_sale, quantity, id_client, id_medicament } as Sale)
      .subscribe(sale => {
        this.getSales();
      });
  }

  delete(sale: Sale): void {
    this.sales = this.sales.filter(h => h !== sale);
    this.saleService.deleteSale(sale.id).subscribe();
  }

}
