import { Component, OnInit } from '@angular/core';

import { Supply } from '../../model/supply';
import { SupplyService } from '../../services/supply.service';

@Component({
  selector: 'app-supplies',
  templateUrl: './supplies.component.html',
  styleUrls: ['./supplies.component.css']
})
export class SuppliesComponent implements OnInit {
  supplies: Supply[] = [];

  constructor(private supplyService: SupplyService) { }

  ngOnInit(): void {
    this.getSupplies();
  }

  getSupplies(): void {
    this.supplyService.getSupplies()
    .subscribe(supplies => this.supplies = supplies);
  }

  add(dateSupply: string, quantity: number, idC: number, idM: number): void {
    dateSupply = dateSupply.trim();
    let dateOfSupply = new Date(dateSupply);
    //quantity = quantity.trim();
    //idC = idC.trim();
    //idM = idM.trim();


    if (!dateOfSupply || !quantity || !idC || !idM) { return; }
    this.supplyService.addSupply({ dateOfSupply, quantity, idC, idM } as Supply)
      .subscribe(supply => {
        this.getSupplies();
      });
  }

  delete(supply: Supply): void {
    this.supplies = this.supplies.filter(h => h !== supply);
    this.supplyService.deleteSupply(supply.id).subscribe();
  }

}
